using Solutions;
using Xunit;
using Xunit.Abstractions;
using System.Text;
using System.Diagnostics;
using FluentAssertions;

namespace Tests
{
    public class LongestCommonSequesnceTests
    {
        private readonly ITestOutputHelper output;

        public LongestCommonSequesnceTests(ITestOutputHelper output)
        {
            this.output = output;
        }

        [Fact]
        public void TestName()
        {
            //Given

            //When

            //Then
        }

        [Theory]
        [InlineData("a", "s", 0)]
        [InlineData("d", "d", 1)]
        [InlineData("abs", "abs", 3)]
        [InlineData("abcde", "ace", 3)]
        [InlineData("abc", "def", 0)]
        [InlineData("aabbccdd", "qbcdqabc", 3)]
        [InlineData("zelohidwdcxilkvytazgfozonwrkbalcpizgtmzuhkbsfefshmtctuvc", "rwjmzoncvihmlmvgdujopwrajuxmjefonivyvkncnwnkjaxkritkporsj", 17)]
        [InlineData("", "", 998)]
        [InlineData("", "", 323)]
        public void DataDrivenTest(string text1, string text2, int expectedMaxLen)
        {
            Stopwatch timer = Stopwatch.StartNew();
            var sut = new LongestCommonSequesnce();
            int maxLen = sut.LongestCommonSubsequence(text1, text2);
            timer.Stop();
            maxLen.Should().Be(expectedMaxLen, $"text1 = '{text1}' text2 = '{text2}'");
            timer.ElapsedMilliseconds.Should().BeLessThan(100);
        }
    }
}